import React, { useState, useEffect } from 'react';
import Layout from './Layout';

function ShowAllUsersBlogs() {
  const [blogs, setBlogs] = useState([]);

  useEffect(() => {
    async function fetchData() {
      try {
        const response = await fetch('http://localhost:5000/AllBlogs');
        if (!response.ok) {
          throw new Error(`HTTP error: ${response.status}`);
        }
        const result = await response.json();
        setBlogs(result.blogs);
      } catch (error) {
        console.error('There was a problem with the fetch operation:', error);
      }
    }
    fetchData();
  }, []);

//   const handleDelete = async (blogId) => {
//     try {
//       const response = await fetch(`http://localhost:5000/DeleteBlog/${blogId}`, {
//         method: 'DELETE',
//       });

//       if (!response.ok) {
//         throw new Error(`HTTP error: ${response.status}`);
//       }
//     } catch (error) {
//       console.error('There was a problem with the delete operation:', error);
//     }
//   };

  return (
    <Layout>
      <div>
        {blogs.map((blog, index) => (
          <div key={index} className='border border-black mt-6 rounded mx-8 flex '>
            <div>
            <h1 className='text-2xl font-bold'>{blog.title}</h1>
            <p className='text-md font-bold my-2'>{blog.snippets}</p>
            <p>{blog.body}</p>
             </div>
             <div>
             <button class="bg-blue-500 hover:bg-blue-700 text-white font-bold py-2 px-4 rounded ">
  Button
</button>
             </div>
          </div>
           
        ))}
      </div>
    </Layout>
  );
}

export default ShowAllUsersBlogs;
