import React, { useState, useEffect } from 'react';
import Layout from './Layout';
import { Link } from 'react-router-dom';

function FrontPage() {
  const [blogs, setBlogs] = useState([]);
  const [error, setError] = useState(null);
  useEffect(() => {
    async function fetchData() {
      try {
        const response = await fetch('http://localhost:5000/Home');
        if (!response.ok) {
          throw new Error(`HTTP error: ${response.status}`);
        }
        const result = await response.json();
        if (response.ok) {
          setBlogs(result.blogs);
        } else {
          setError(result.error);
        }
      } catch (error) {
        console.error('There was a problem with the fetch operation:', error);
      }
    }
    fetchData();
  }, []);

  return (
    <Layout>
      {/* <div>

        {blogs.map((blog, index) => (
          <div key={blogs._id} className='border border-black mt-6 rounded mx-8'>
           <Link to={`/details/${blog._id}`}><h1 className='text-2xl font-bold'>{blog.title}</h1></Link>
            <p className='text-md font-bold my-2'>{blog.snippets}</p>
            <p>{blog.body}</p>
          </div>
        ))}
      </div> */}
      <div>
      {error && <p>Error: {error}</p>}
      {blogs.length > 0 ? (
        blogs.map((blog) => (
          <div key={blog._id}>
            <h2>{blog.title}</h2>
            <p>{blog.body}</p>
            {/* Render other details as needed */}
          </div>
        ))
      ) : (
        <p>No blogs found <Link to="/Create"> <span className='text-yellow-500 text-lg'>Create a New Blog</span> </Link></p>

      )}
    </div>
    </Layout>
  );
}

export default FrontPage;
