import React, { useState, useEffect } from "react";
import Layout from "./Layout";
import { Link } from "react-router-dom";

function ShowAllUsersBlogs() {
  const [blogs, setBlogs] = useState([]);

  useEffect(() => {
    async function fetchData() {
      try {
        const response = await fetch("http://localhost:5000/AllBlogs");
        if (!response.ok) {
          throw new Error(`HTTP error: ${response.status}`);
        }
        const result = await response.json();
        setBlogs(result.blogs);
      } catch (error) {
        console.error("There was a problem with the fetch operation:", error);
      }
    }
    fetchData();
  }, []);
  return (
    <Layout>
      <div>
        {blogs.map((blog, index) => (
          <div key={index} className="flex relative">
            <div className="w-2 bg-orange-500 h-20 ml-8 mt-12"></div>
            <div className="mt-12 ml-4">
              <Link to={`/Detail/${blog._id}`}>
                <h2 className="text-xl font-bold">{blog.title}</h2>
                <p>{blog.snippets}</p>
                <p>{blog.body}</p>
              </Link>

            </div>
            <button className="absolute top-4 right-0 mt-2 mr-8 p-2 text-white rounded-full">
              {/* Add your delete icon here */}
              üóëÔ∏è
            </button>
          </div>
        ))}
      </div>
    </Layout>
  );
}

export default ShowAllUsersBlogs;
