import { useNavigate } from "react-router-dom";
import axios from 'axios';
import React, { useState} from 'react'
import Layout from './Layout'

export default function CreateBlog() {
 const navigate = useNavigate();
  const [title, setTitle] = useState("");
 const [snippets, setSnippets] = useState("");
 const [body, setBody] = useState("");

 const handleClick = () => {
    axios
      .post("http://localhost:5000/Create", {
        title: title,
        snippets: snippets,
        body: body,
      })
      .then((res) => {
        if (res) {
          setTitle(res.data.title);
          setSnippets(res.data.Snips);
          setBody(res.data.body);

          navigate("/Home");
          console.log("Saved");
        }
      })
      .catch((error) => {
        console.error(error);
      });
 };
  return (
    <Layout>
    <div>
        <input type='text' placeholder='Enter The Title Of Blog' onChange={(event)=>{
            setTitle(event.target.value)
        }} ></input><br>
        </br>
        <br></br>
        <input type='text' placeholder='Enter The Snippets Of Blog' onChange={(event)=>{
            setSnippets(event.target.value)
        }}></input><br></br>
        <br></br>
         <textarea placeholder='enter the blog Content' onChange={(event)=>{
            setBody(event.target.value)
        }}>
         </textarea>
         <button onClick={handleClick}>
            Post a Blogs
         </button>
    </div>
    </Layout>
  )
}
